				try {
					string scriptDirectory;
					RenderTemplate template;
					if (!CheckEnvironment(vegas, out scriptDirectory, out template)) return;

					var frameCount = (string)Registry.GetValue(Template.DataPath, "FrameCount", "");
					var defaultCount = 1;
					if (frameCount != "") {
