							var value = int.Parse(frameCount);
							if (value > 0) {
								defaultCount = value;
							}
						} catch (Exception) {
							// ignore
						}
					}

					var prompt = new Form {
						Width = 500,
						Height = 160,
						Text = Lang.str.datamoshing_parameters,
					};
					Template.OptimizePrompt(prompt, ImageBase64.DatamoshIcon);
