				else state = CheckState.Indeterminate;
			}
			selectedYtpEffects = selected.ToArray();
			if (state == null) return;
			YtpEnableAllEffectsCheck.CheckState = (CheckState)state;
			YtpEffectsGroup.Text = Lang.str.effect + " (" + selected.Count + ")";
